mapper.select:
select * from (
select rownum rnum, data.* from (
select bnum,pnum,pprice,pamount,pname,mid,maddr,state,retake,to_char(bdate,'YYYY-MM-DD') bdate
from purchase where pname like '%' || #{searchValue} || '%' and mid=#{userId}
order by bnum desc) data)

where rnum>=#{start} and rnum<=#{end}
